import { useEffect } from 'react';
import { useState, useRef } from 'react';

const SimpleInput = (props) => {
  const [enteredName, setEnteredName] = useState('');
  const [nameIsValid, setNameIsValid] = useState(false)
  const [nameIsTouched, setNameIsTouched] = useState(false)
  const inputNameRef = useRef();

  useEffect(() => {
    if (nameIsValid)
    {
      console.log('useEffect RUNNSSS');
    }
  }, [nameIsValid])

  const nameChangeHandler = (event) => {
    setEnteredName(event.target.value);
    setNameIsTouched(true)
  };

  const formSubmitHandler = (event) => {
    event.preventDefault();

    if (enteredName.trim() === '' || enteredName.trim().length < 6)
    {
      setNameIsValid(false)
      return;
    } else
    {
      setNameIsValid(true)
    }

    console.log(enteredName);

    console.log(inputNameRef.current.value);

    setEnteredName('');
    console.log('submitted successfully');

  }
  const nameInputClasses = nameIsValid && nameIsTouched ? 'form-control' : 'form-control invalid';

  return (
    <form onSubmit={formSubmitHandler}>
      <div className={nameInputClasses}>
        <label htmlFor='name'>Your Name</label>
        <input ref={inputNameRef} type='text' id='name' onChange={nameChangeHandler} value={enteredName} />
        {!nameIsValid && <p className='error-text'>please enter a valid name</p>}
      </div>
      <div className="form-actions">
        <button>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
