import { useState } from 'react';

const SimpleInput = (props) => {
  const [enteredName, setEnteredName] = useState('');
  // const [nameIsValid, setNameIsValid] = useState(false);
  const [nameIsTouched, setNameIsTouched] = useState(false);
  const [enteredEmail, setEnteredEmail] = useState('');
  const [emailIsTouched, setEmailIsTouched] = useState(false);


  const nameIsValid = enteredName.trim() !== '' && enteredName.trim().length >= 6;
  const emailIsValid = enteredEmail.trim() !== "" && enteredEmail.includes('@');

  const nameInputIsInvalid = !nameIsValid && nameIsTouched;
  const emailInputIsInvalid = !emailIsValid && emailIsTouched;

  let formIsValid = false

  if (nameIsValid && emailIsValid)
  {
    formIsValid = true
  };



  const nameChangeHandler = (event) => {
    setEnteredName(event.target.value);
  };
  const onBlurHandler = (event) => {
    setNameIsTouched(true);
    setEmailIsTouched(true);
  };

  const emailChangeHandler = (event) => {
    setEnteredEmail(event.target.value)
  };

  const formSubmitHandler = (event) => {
    event.preventDefault();
    setNameIsTouched(true)
    setEmailIsTouched(true)
    if (!nameIsValid || !emailIsValid)
    {

      return;
    }
    setEnteredName('');
    setEnteredEmail('')
    console.log('submitted successfully');
    setNameIsTouched(false)
    setEmailIsTouched(false)
  };

  const nameInputClasses = !nameInputIsInvalid ? 'form-control' : 'form-control invalid';
  const emailInputClasses = !emailInputIsInvalid ? 'form-control' :
    'form-control invalid';

  return (
    <form onSubmit={formSubmitHandler}>
      <div className={nameInputClasses}>
        <label htmlFor='name'>Your Name</label>
        <input type='text' id='name' onChange={nameChangeHandler} value={enteredName}
          onBlur={onBlurHandler} />
        {nameInputIsInvalid && <p className='error-text'>please enter a valid name</p>}
      </div>
      <div className={emailInputClasses}>
        <label htmlFor='email'>Your Email</label>
        <input
          type='email'
          id='email'
          onChange={emailChangeHandler}
          value={enteredEmail}
          onBlur={onBlurHandler}
        />
        {emailInputIsInvalid && <p className='error-text'>please enter a valid email</p>}
      </div>
      <div className="form-actions">
        <button disabled={!formIsValid}>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
