import { useState } from 'react';
import useInput from '../hooks/use-input';

const SimpleInput = (props) => {

  const { value: enteredName, hasError: nameInputHasError, isValid: nameIsValid, valueChangeHandler: nameChangedHandler, inputBlurHandler: nameBlurHandler } = useInput((value) => {
    value.trim() !== '' && value.trim().length >= 6
  });
  const { value: enteredEmail, hasError: emailInputHasError, isValid: emailIsValid, valueChangeHandler: emailChangedHAndler, inputBlurHandler: emailBlurHandler } = useInput(value => { value.trim() !== "" && value.includes('@') });

  // const [enteredName, setEnteredName] = useState('');
  // const [nameIsValid, setNameIsValid] = useState(false);
  // const [nameIsTouched, setNameIsTouched] = useState(false);
  // const [enteredEmail, setEnteredEmail] = useState('');
  // const [emailIsTouched, setEmailIsTouched] = useState(false);




  let formIsValid = false

  if (nameIsValid && emailIsValid)
  {
    formIsValid = true
  };









  const formSubmitHandler = (event) => {
    event.preventDefault();
    setNameIsTouched(true)
    setEmailIsTouched(true)
    if (!nameIsValid || !emailIsValid)
    {

      return;
    }
    setEnteredName('');
    setEnteredEmail('')
    console.log('submitted successfully');
    setNameIsTouched(false)
    setEmailIsTouched(false)
  };

  const nameInputClasses = !nameInputHasError ? 'form-control' : 'form-control invalid';
  const emailInputClasses = !emailInputHasError ? 'form-control' :
    'form-control invalid';

  return (
    <form onSubmit={formSubmitHandler}>
      <div className={nameInputClasses}>
        <label htmlFor='name'>Your Name</label>
        <input type='text' id='name' onChange={nameChangedHandler} value={enteredName}
          onBlur={nameBlurHandler} />
        {nameInputHasError && <p className='error-text'>please enter a valid name</p>}
      </div>
      <div className={emailInputClasses}>
        <label htmlFor='email'>Your Email</label>
        <input
          type='email'
          id='email'
          onChange={emailChangedHAndler}
          value={enteredEmail}
          onBlur={emailBlurHandler}
        />
        {emailInputHasError && <p className='error-text'>please enter a valid email</p>}
      </div>
      <div className="form-actions">
        <button disabled={!formIsValid}>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
